code.foreach:
DELIMITER $$ DROP PROCEDURE IF EXISTS `sp_$Table.Name$_Add`$$ /******************************************************************************* **增加一条记录 *******************************************************************************/ CREATE PROCEDURE `sp_$Table.Name$_Add` (
in_$Field.Name$ $Field.FieldType$($Field.Length$),
in_$Field.Name$ $Field.FieldType$,
in_$Field.Name$ $Field.FieldType$($Field.Length$),
in_$Field.Name$ $Field.FieldType$,
) BEGIN INSERT INTO `$Table.Name$`(
`$Field.Name$`,
`$Field.Name$`,
)VALUES(
in_$Field.Name$,
in_$Field.Name$,
); END$$ DELIMITER ;
DELIMITER $$ DROP PROCEDURE IF EXISTS `sp_$Table.Name$_Update`$$ /******************************************************************************* **修改一条记录 *******************************************************************************/ CREATE PROCEDURE `sp_$Table.Name$_Update` (
in_$Field.Name$ $Field.FieldType$($Field.Length$),
in_$Field.Name$ $Field.FieldType$,
) BEGIN UPDATE `$Table.Name$` SET `$Field.Name$`=in_$Field.Name$, WHERE `$Field.Name$`=in_$Field.Name$ AND; END$$ DELIMITER ;
DELIMITER $$ DROP PROCEDURE IF EXISTS `sp_$Table.Name$_Delete`$$ /******************************************************************************* **删除一条记录 *******************************************************************************/ CREATE PROCEDURE `sp_$Table.Name$_Delete` (
in_$Field.Name$ $Field.FieldType$($Field.Length$),
in_$Field.Name$ $Field.FieldType$,
) BEGIN DELETE FROM `$Table.Name$` WHERE `$Field.Name$`=in_$Field.Name$ AND; END$$ DELIMITER ;
DELIMITER $$ DROP PROCEDURE IF EXISTS `sp_$Table.Name$_Exists`$$ /******************************************************************************* **是否已经存在 *******************************************************************************/ CREATE PROCEDURE `sp_$Table.Name$_Exists` (
in_$Field.Name$ $Field.FieldType$($Field.Length$),
in_$Field.Name$ $Field.FieldType$,
) BEGIN SELECT COUNT(*) FROM `$Table.Name$` WHERE `$Field.Name$`=in_$Field.Name$ AND; END$$ DELIMITER ;
DELIMITER $$ DROP PROCEDURE IF EXISTS `sp_$Table.Name$_GetCount`$$ /******************************************************************************* **获取记录条数 *******************************************************************************/ CREATE PROCEDURE `sp_$Table.Name$_GetCount`() BEGIN SELECT COUNT(*) FROM `$Table.Name$`; END$$ DELIMITER ;
DELIMITER $$ DROP PROCEDURE IF EXISTS `sp_$Table.Name$_GetModel`$$ /******************************************************************************* **得到一个实体 *******************************************************************************/ CREATE PROCEDURE `sp_$Table.Name$_GetModel` (
in_$Field.Name$ $Field.FieldType$($Field.Length$),
in_$Field.Name$ $Field.FieldType$,
) BEGIN SELECT * FROM `$Table.Name$` WHERE `$Field.Name$`=in_$Field.Name$ AND; END$$ DELIMITER ;
DELIMITER $$ DROP PROCEDURE IF EXISTS `sp_$Table.Name$_GetAllList`$$ /******************************************************************************* **得到所有实体 *******************************************************************************/ CREATE PROCEDURE `sp_$Table.Name$_GetAllList`() BEGIN SELECT * FROM `$Table.Name$`; END$$ DELIMITER ;
